<script setup>
import { useAuthStore } from '@/stores/auth/AuthStore'
import sideNavIcon from '@/components/navigation/sideNavIcon.vue'
import { onMounted, ref } from 'vue';
import { storeToRefs } from 'pinia';
import { useRealtimeNofiticationStore } from '@/stores/realtime/realtimeNotificationStore'
import { useViolationsStore } from '@/stores/violation/violationsStore'
import { onClickOutside } from '@vueuse/core'

const target = ref(null)

onClickOutside(target, () => {
  isNotificationShow.value = false
})

const realtimeNotificationStore = useRealtimeNofiticationStore()
const violationsStore = useViolationsStore()

const { getViolationNotificationIsLoading, violationsNotification, notificationStatus } = storeToRefs(useViolationsStore())
const { isNewMessage } = storeToRefs(useRealtimeNofiticationStore())

const isNotificationShow = ref(false)

function viewNotifications() {
  isNewMessage.value = false
  isNotificationShow.value = true
  violationsStore.getViolationNotification()
}
const props = defineProps({
  isDashboardActive: {
    type: Boolean,
    default: false
  },
  isDumptruckActive: {
    type: Boolean,
    default: false
  },
  isExcavatorActive: {
    type: Boolean,
    default: false
  },
  isLightVehicleActive: {
    type: Boolean,
    default: false
  },
  isDMActive: {
    type: Boolean,
    default: false
  },
  isViolationActive: {
    type: Boolean,
    default: false
  },
  isReportActive: {
    type: Boolean,
    default: false
  },
  isSettingActive: {
    type: Boolean,
    default: false
  },
}) 

  const authStore = useAuthStore();

  async function signOut() {
      await authStore.signOut();
  }
  const goBack = () => {
      router.go(-1);
  };
 
  const title = ref()

  onMounted(() => {
    realtimeNotificationStore.getRealtimeNotification()
    if (props.isDashboardActive) {
      title.value = 'Dashboard'
    }
    if (props.isDumptruckActive) {
      title.value = 'Monitoring Dump Truck'
    }
    if (props.isExcavatorActive) {
      title.value = 'Monitoring Excavator'
    }
    if (props.isLightVehicleActive) {
      title.value = 'Monitoring Light Vehicle'
    }
    if (props.isDMActive) {
      title.value = 'Device Management'
    }
    if (props.isViolationActive) {
      title.value = 'Violation'
    }
    if (props.isReportActive) {
      title.value = 'Report'
    }
    if (props.isSettingActive) {
      title.value = 'Data Master'
    }
  })
  
</script>

